SELECT income_date as incomDate, count(DISTINCT detail.user_id) as countSeller, sum(source_amount) /100 as money, sum(amout) / 100 as myMoney, count(DISTINCT detail.user_id) as countSmallSeller, sum(source_amount / 100) as smallSellerMoney, sum(amout / 100) as mySellerMoney, sum(amout / 100) as sum FROM agent_income_detail detail
WHERE detail.user_id in
#{item}
GROUP BY detail.income_date order by detail.income_date DESC
limit #{page.begin}, #{page.end}
