		select

		from
		moumou.orders_detail_${orderTableNum}_${tableNum}


				and id= #{id,jdbcType=BIGINT}


				and order_detail_no= #{orderDetailNo,jdbcType=VARCHAR}


				and title= #{title,jdbcType=VARCHAR}


				and goods_id= #{goodsId,jdbcType=INTEGER}


				and order_no= #{orderNo,jdbcType=VARCHAR}


				and order_desc= #{orderDesc,jdbcType=VARCHAR}


				and order_bak= #{orderBak,jdbcType=VARCHAR}


				and order_state= #{orderState,jdbcType=TINYINT}


				and goods_count= #{goodsCount,jdbcType=INTEGER}


				and create_time= #{createTime,jdbcType=TIMESTAMP}


				and modify_time= #{modifyTime,jdbcType=TIMESTAMP}


				and unit_pirce= #{unitPirce,jdbcType=INTEGER}


				and count_price= #{countPrice,jdbcType=INTEGER}


				and order_count_price=
				#{orderCountPrice,jdbcType=INTEGER}


				and order_unit_price= #{orderUnitPrice,jdbcType=INTEGER}


				and start_time= #{startTime,jdbcType=TIMESTAMP}


				and end_time= #{endTime,jdbcType=TIMESTAMP}


				and consume_type= #{consumeType,jdbcType=TINYINT}


				and insurance_id= #{insuranceId,jdbcType=BIGINT}


				and snapshop_id= #{snapshopId,jdbcType=BIGINT}



				and course_public_id = #{coursePublicId,jdbcType=BIGINT}


				and teacher_id = #{teacherId,jdbcType=INTEGER}



				and teacher_name = #{teacherName,jdbcType=VARCHAR}



				and schedule_state= #{scheduleState,jdbcType=TINYINT}


				and is_comment= #{isComment,jdbcType=TINYINT}


				and baby_ids = #{babyIds,jdbcType=VARCHAR}


				and baby_ids like CONCAT('${babyIdLike}','%' )


				and order_state in (${orderStateIn})


				and extra_babys_count =
				#{extraBabysCount,jdbcType=INTEGER}


				and course_public_ids =
				#{coursePublicIds,jdbcType=VARCHAR}


				and allot_time = #{allotTime,jdbcType=VARCHAR}


				and goods_ids = #{goodsIds,jdbcType=VARCHAR}



        order by  ${order}

