select
count(1)
from
moumou.withdrawal_history_order


		and id= #{id,jdbcType=BIGINT}


		and withdrawl_history_order_no=
		#{withdrawlHistoryOrderNo,jdbcType=VARCHAR}


		and batch_no= #{batchNo,jdbcType=VARCHAR}


		and price= #{price,jdbcType=INTEGER}


		and account= #{account,jdbcType=VARCHAR}


		and withdrawal_channel=
		#{withdrawalChannel,jdbcType=TINYINT}


		and third_party_pay_no=
		#{thirdPartyPayNo,jdbcType=VARCHAR}


		and create_time= #{createTime,jdbcType=TIMESTAMP}


		and modify_time= #{modifyTime,jdbcType=TIMESTAMP}


		and finish_time= #{finishTime,jdbcType=TIMESTAMP}


		and state= #{state,jdbcType=TINYINT}


		and freeze_balance= #{freezeBalance,jdbcType=INTEGER}


		and user_id= #{userId,jdbcType=BIGINT}


		and account_name= #{accountName,jdbcType=VARCHAR}


		and type= #{type,jdbcType=TINYINT}

select count(1) as count from moumou.withdrawal_history_order
and user_id=#{userId,jdbcType=BIGINT}
and DATE_FORMAT(create_time, '%Y%m' ) = DATE_FORMAT( CURDATE( ) , '%Y%m' ) group by DATE_FORMAT(create_time,'%Y%m')
